UPDATE `eve_action` SET `ea_id` = 1043,`ea_systemid` = NULL,`ea_actionname` = '隐藏卡板指定区块',`ea_correspscript` = '//隐藏卡板中的指定块\r\n//var tabPanel = ''TabPanel35790'';\r\n//var formID = ''form143'';\r\n//var hiddenField = ''臻尚白金卡'';\r\n//var control = {''如达到申请条件，阁下是否愿意申请臻尚白金卡'':''0'',''臻尚卡品牌'':''C''};\r\n\r\ntry{\r\n  var legend = Ext.query(''form[id=''+formID+'']>fieldset>legend'', Ext.getDom(tabPanelID));\r\n  for (var i=0; i<legend.length; i++) {\r\n    if (legend[i].innerText === hiddenField) {\r\n      for (var index in control) {\r\n        var tmp = Ext.query(''input[name=''+index+''][value=''+control[index]+'']'', legend[i].parentElement);\r\n        if(tmp[0]){\r\n            tmp[0].checked = true;\r\n        }\r\n        \r\n        legend[i].parentElement.style.display = ''none'';\r\n\r\n      }\r\n    }\r\n  }\r\n\r\n\r\n}catch(e){}',`ea_requestparamexpre` = 'tabPanelID,formID,hiddenField,control' WHERE `ea_id` = 1043;
select * from eve_action where 1=1;